import React, { useState, useEffect, useRef } from 'react';
import './Page1.css';

const SplitWindow = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });
  const [showPassword, setShowPassword] = useState(false); // pour toggle mot de passe

  const particlesCreated = useRef(false);

  useEffect(() => {
    if (!particlesCreated.current) {
      const particlesContainer = document.getElementById('particles');
      if (particlesContainer) {
        const particleCount = 20;
        for (let i = 0; i < particleCount; i++) {
          const particle = document.createElement('div');
          particle.className = 'particle';
          particle.style.left = Math.random() * 100 + '%';
          particle.style.animationDelay = Math.random() * 8 + 's';
          particle.style.animationDuration = (Math.random() * 3 + 5) + 's';
          particlesContainer.appendChild(particle);
        }
        particlesCreated.current = true;
      }
    }

    const handleMouseMove = (e) => {
      const cursor = e.clientX / window.innerWidth;
      const leftPane = document.querySelector('.left-pane');
      if (leftPane) {
        leftPane.style.transform = `perspective(1000px) rotateY(${cursor * 2}deg)`;
      }
    };
    document.addEventListener('mousemove', handleMouseMove);

    // On ne lance plus d'animation typing, placeholders fixes

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  const animateRecycle = () => {
    const icon = document.querySelector('.recycle-icon');
    if (icon) {
      icon.style.animation = 'none';
      setTimeout(() => {
        icon.style.animation = 'float 4s ease-in-out infinite';
      }, 10);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.username && formData.password) {
      setIsLoading(true);

      setTimeout(() => {
        setIsLoading(false);
        setShowSuccess(true);

        setTimeout(() => {
          setShowSuccess(false);
          setFormData({ username: '', password: '' });
        }, 3000);
      }, 2000);
    }
  };

  const showForgotPassword = () => {
    alert('Fonctionnalit√© de r√©cup√©ration de mot de passe √† venir !');
  };

  const toggleShowPassword = () => {
    setShowPassword(prev => !prev);
  };

  return (
    <div className="container">
      <div className="left-pane">
        <div className="particles" id="particles"></div>
        <div className="recycle-icon" onClick={animateRecycle}>
          ‚ôª
        </div>
        <div className="left-content">
          <h1>Ashinglo</h1>
          <p>De la pollution √† la solution</p>
        </div>
      </div>

      <div className="right-pane">
        <div className="logo-container">
          <div className="logo">
            <img src="/images/logo.png" alt="Logo Ashinglo" />
          </div>
        </div>

        <div className="login-container">
          {showSuccess && (
            <div className="success-message">
              ‚úì Connexion r√©ussie ! Redirection en cours...
            </div>
          )}

          <div className="welcome-text">
            <h2>Bienvenue</h2>
            <p>Connectez-vous √† votre espace</p>
          </div>

          <form className="login-form" onSubmit={handleSubmit}>
            <div className="input-group">
              <label htmlFor="username">Nom d'utilisateur</label>
              <input
                type="text"
                id="username"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                required
                autoComplete="off"
                placeholder="eennttrreezz vvoottrree nnoomm"
              />
            </div>

            <div className="input-group" style={{ position: 'relative' }}>
              <label htmlFor="password">Mot de passe</label>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                required
                autoComplete="off"
                placeholder="eennttrreezz vvoottrree mmoott dde ppaassssee"
              />
              <button
                type="button"
                onClick={toggleShowPassword}
                style={{
                  position: 'absolute',
                  right: '10px',
                  top: '50%',
                  transform: 'translateY(-50%)',
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '14px',
                  color: '#555'
                }}
                aria-label={showPassword ? 'Cacher mot de passe' : 'Afficher mot de passe'}
              >
                {showPassword ? 'üôà' : 'üëÅÔ∏è'}
              </button>
            </div>

            <button type="submit" className="login-btn" disabled={isLoading}>
              <span>{isLoading ? 'Connexion...' : 'Connexion'}</span>
              {isLoading && <div className="loading"></div>}
            </button>
          </form>

          <div className="forgot-password">
            <a href="#" onClick={showForgotPassword}>Mot de passe oubli√© ?</a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SplitWindow;
